#include <stdint.h>



int main(void)
{
	uint32_t *pClkCtrolreg = (uint32_t*)0x40023830;
	uint32_t *pPortDModeReg = (uint32_t*)0x40020C00;
	uint32_t *pPortDoutReg = (uint32_t*)0x40020C14;

	//1. Enable clock for GPIOD peripheral in the AHB1ENR set 3rd bit position "<<" = left shit (increase value). ">>" = right shift (decrease value)
	*pClkCtrolreg |= (1 << 3);
	//2. config mode of IO pin as output
	//a. clear 24th and 25th bit positions (CLEAR)
	*pPortDModeReg &= ~(3 << 24); // 3 in binary = 11
	//b. set 24th bit position to 1 (set)
	*pPortDModeReg |= (1 << 24);

	while(1)
	{
		//3. SET 12th bit of output data register to set I/O pin-12 to HIGH
		//Toggle LED ON
		*pPortDoutReg |= (1 <<12);
		//Delay
		for( uint32_t i=0; i < 200000; i++);

		//Toggle LED OFF
		*pPortDoutReg &= ~(1<<12);

		//Delay
		for( uint32_t i=0; i < 200000; i++);
	}





    /* Loop forever */
	while(1);
}
