#include <stdint.h>

int main(void) {
	uint32_t *pClkCtrolreg = (uint32_t*) 0x40023830;
	uint32_t *pPortDModeReg = (uint32_t*) 0x40020C00;
	uint32_t *pPortDoutReg = (uint32_t*) 0x40020C14;

	uint32_t *pPortAModeReg = (uint32_t*) 0x40020000;
	uint32_t *pPortAInReg = (uint32_t*) 0x40020010;

	//1. Enable clock for GPIOD, GPIOA peripheral in the AHB1ENR  "<<" = left shit (increase value). ">>" = right shift (decrease value)
	*pClkCtrolreg |= (1 << 3);
	*pClkCtrolreg |= (1 << 0);

	//Configure PD12 as output
	*pPortDModeReg &= ~(3 << 24); // 3 in binary = 11

	//b. set 24th bit position to 1 (set)
	*pPortDModeReg |= (1 << 24);

	//Cofigure PA0 as input mode (GPIOA mode register)
	*pPortAModeReg &= ~(3 << 0);

	while (1) {

		//read pin status of pin PA0 (GPIOA input data register)
		uint8_t pinStatus = (uint8_t) (*pPortAInReg & 0x1);

		if (pinStatus) {
			//Toggle LED ON
			*pPortDoutReg |= (1 << 12);

		} else {
			*pPortDoutReg &= ~(1 << 12);

		}
	}

	/* Loop forever */
	while (1);		;
}
